import { httpAction } from '@/api/manage';
import pick from 'lodash.pick';
import JDate from '@/components/jeecg/JDate';
import JDictSelectTag from "@/components/dict/JDictSelectTag";
export default {
  name: "SaasServiceModal",
  components: {
    JDate: JDate,
    JDictSelectTag: JDictSelectTag
  },
  data: function data() {
    return {
      form: this.$form.createForm(this),
      title: "操作",
      width: 800,
      visible: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      validatorRules: {
        product: {
          rules: [{
            required: true,
            message: '请输入产品名称!'
          }]
        },
        purchaseType: {
          rules: [{
            required: true,
            message: '请输入购买方式!'
          }]
        },
        purchaser: {
          rules: [{
            required: true,
            message: '请输入购买者!'
          }]
        },
        startTime: {
          rules: [{
            required: true,
            message: '请输入服务开始时间!'
          }]
        },
        endTime: {
          rules: [{
            required: true,
            message: '请输入服务结束时间!'
          }]
        },
        enterpriseCode: {},
        enterpriseName: {},
        serviceState: {
          rules: [{
            required: true,
            message: '请输入服务状态!'
          }]
        },
        subDomain: {},
        remark1: {},
        remark2: {},
        remark3: {},
        remark4: {},
        remark5: {},
        remark6: {},
        remark7: {},
        remark8: {},
        remark9: {},
        remark10: {},
        remark11: {},
        remark12: {},
        remark13: {},
        remark14: {},
        remark15: {}
      },
      url: {
        add: "/saas/saasService/add",
        edit: "/saas/saasService/edit"
      }
    };
  },
  created: function created() {},
  methods: {
    add: function add() {
      this.edit({});
    },
    edit: function edit(record) {
      var _this = this;

      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, 'product', 'purchaseType', 'purchaser', 'startTime', 'endTime', 'enterpriseCode', 'enterpriseName', 'serviceState', 'subDomain', 'remark1', 'remark2', 'remark3', 'remark4', 'remark5', 'remark6', 'remark7', 'remark8', 'remark9', 'remark10', 'remark11', 'remark12', 'remark13', 'remark14', 'remark15'));
      });
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this2 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this2.model.id) {
            httpurl += _this2.url.add;
            method = 'post';
          } else {
            httpurl += _this2.url.edit;
            method = 'put';
          }

          var formData = Object.assign(_this2.model, values);
          console.log("表单提交数据", formData);
          httpAction(httpurl, formData, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    },
    popupCallback: function popupCallback(row) {
      this.form.setFieldsValue(pick(row, 'product', 'purchaseType', 'purchaser', 'startTime', 'endTime', 'enterpriseCode', 'enterpriseName', 'serviceState', 'subDomain', 'remark1', 'remark2', 'remark3', 'remark4', 'remark5', 'remark6', 'remark7', 'remark8', 'remark9', 'remark10', 'remark11', 'remark12', 'remark13', 'remark14', 'remark15'));
    }
  }
};
