import { httpAction } from '@/api/manage';
import pick from 'lodash.pick';
import JSelectDepart from '@/components/jeecgbiz/JSelectDepart';
import JSelectUserByDep from '@/components/jeecgbiz/JSelectUserByDep';
import JDictSelectTag from "@/components/dict/JDictSelectTag";
export default {
  name: "ContractModal",
  components: {
    JSelectDepart: JSelectDepart,
    JSelectUserByDep: JSelectUserByDep,
    JDictSelectTag: JDictSelectTag
  },
  data: function data() {
    return {
      form: this.$form.createForm(this),
      title: "操作",
      width: 800,
      visible: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      validatorRules: {
        contractCode: {
          rules: [{
            required: true,
            message: '请输入合同编号!'
          }]
        },
        contractName: {
          rules: [{
            required: true,
            message: '请输入合同名称!'
          }]
        },
        dealMan: {},
        dealDept: {},
        amount: {},
        contractType: {}
      },
      url: {
        add: "/module/contract/add",
        edit: "/module/contract/edit"
      }
    };
  },
  created: function created() {},
  methods: {
    add: function add() {
      this.edit({});
    },
    edit: function edit(record) {
      var _this = this;

      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, 'contractCode', 'contractName', 'dealMan', 'dealDept', 'amount', 'contractType'));
      });
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this2 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this2.model.id) {
            httpurl += _this2.url.add;
            method = 'post';
          } else {
            httpurl += _this2.url.edit;
            method = 'put';
          }

          var formData = Object.assign(_this2.model, values);
          console.log("表单提交数据", formData);
          httpAction(httpurl, formData, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    },
    popupCallback: function popupCallback(row) {
      this.form.setFieldsValue(pick(row, 'contractCode', 'contractName', 'dealMan', 'dealDept', 'amount', 'contractType'));
    }
  }
};
