import JSelectBizComponentModal from "./JSelectBizComponentModal";
export default {
  name: 'JSelectBizComponent',
  components: {
    JSelectBizComponentModal: JSelectBizComponentModal
  },
  props: {
    value: {
      type: String,
      default: ''
    },

    /** 是否返回 id，默认 false，返回 code */
    returnId: {
      type: Boolean,
      default: false
    },
    placeholder: {
      type: String,
      default: '请选择'
    },
    disabled: {
      type: Boolean,
      default: false
    },
    // 是否支持多选，默认 true
    multiple: {
      type: Boolean,
      default: true
    },
    // 是否显示按钮，默认 true
    buttons: {
      type: Boolean,
      default: true
    },

    /* 可复用属性 */
    // 被选择的名字，例如选择部门就填写'部门'
    name: {
      type: String,
      default: ''
    },
    // list 接口地址
    listUrl: {
      type: String,
      required: true,
      default: ''
    },
    // 显示的 Key
    displayKey: {
      type: String,
      default: null
    },
    // 返回的 key
    returnKeys: {
      type: Array,
      default: function _default() {
        return ['id', 'id'];
      }
    },
    // 选择按钮文字
    selectButtonText: {
      type: String,
      default: '选择'
    },
    // 查询条件文字
    queryParamText: {
      type: String,
      default: null
    },
    // columns
    columns: {
      type: Array,
      default: function _default() {
        return [];
      }
    }
  },
  data: function data() {
    return {
      selectValue: [],
      selectOptions: [],
      visible: false
    };
  },
  computed: {
    valueKey: function valueKey() {
      return this.returnId ? this.returnKeys[0] : this.returnKeys[1];
    }
  },
  watch: {
    value: {
      immediate: true,
      handler: function handler(val) {
        if (val) {
          this.selectValue = val.split(',');
        } else {
          this.selectValue = [];
        }
      }
    },
    selectValue: {
      deep: true,
      handler: function handler(val) {
        var data = val.join(',');
        this.$emit('input', data);
        this.$emit('change', data);
      }
    }
  },
  methods: {}
};
